import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addBusiness, deductBalance } from '../store/slices/business-slice';
import './styles/Business.css';
import { RootState } from '../store/store';

const Business: React.FC = () => {
  const dispatch = useDispatch();
  const businesses = useSelector((state: RootState) => state.business.businesses);
  const balance = useSelector((state: RootState) => state.business.balance);

  const handleAddBusiness = (business: any) => {
    if (balance >= business.price) {
      dispatch(deductBalance(business.price));
      dispatch(addBusiness(business));
    } else {
      alert("Not enough balance to buy this business");
    }
  };

  return (
    <div className="business-container">
      <h2>Buy a Business</h2>
      <div className="business-selection">
        <button onClick={() => handleAddBusiness({ name: 'Soviet Press Kiosk', price: 100, profit: 10, productionTime: 5 })}>
          <img src="/assets/images/panelka1.png" alt="Soviet Press Kiosk" />
          <span>Soviet Press Kiosk</span>
        </button>
        <button onClick={() => handleAddBusiness({ name: 'Racketeering', price: 500, profit: 50, productionTime: 15 })}>
          <img src="/assets/images/panelka2.png" alt="Racketeering" />
          <span>Racketeering</span>
        </button>
        <button onClick={() => handleAddBusiness({ name: 'Black Market', price: 1000, profit: 100, productionTime: 30 })}>
          <img src="/assets/images/panelka3.png" alt="Black Market" />
          <span>Black Market</span>
        </button>
        <button onClick={() => handleAddBusiness({ name: 'Video Rental', price: 2000, profit: 200, productionTime: 60 })}>
          <img src="/assets/images/90s1.png" alt="Video Rental" />
          <span>Video Rental</span>
        </button>
      </div>
      <h2>Owned Businesses</h2>
      <ul>
        {businesses.map((business, index) => (
          <li key={index}>
            <img src={`/assets/images/${business.name.toLowerCase().replace(/ /g, '-')}.png`} alt={business.name} />
            <span>{business.name}</span>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Business;
